import React from 'react';
import styled from 'styled-components';

const Button = styled.button`
  background-color: #dc3545;
  color: white;
  border: none;
  padding: 0.75em 1.5em;
  border-radius: 4px;
  cursor: pointer;
  font-size: 1em;
  transition: background-color 0.1s ease-in;
  margin: 5 5em;

  &:hover {
    background-color: #c82333;
  }

  &:focus {
    outline: none;
    box-shadow: 0 0 8px rgba(220, 53, 69, 0.6);
  }
`;

interface ValidateButtonProps {
  formData: {
    accessKey: string;
    secretKey: string;
    email: string;
    projectName: string;
    clusterList: string;
    clusterName: string;
    apiEndpoint: string;
    authData: string;
    instanceList: string;
    primaryEndpoint: string;
    standbyEndpoint: string;
    dockerImageName: string;
    dockerJavaVersion: string;
  };
}

const ValidateButton: React.FC<ValidateButtonProps> = ({ formData }) => {
  const validate = () => {
    let isValid = true;
    const errors = [];

    if (!formData.accessKey) {
      isValid = false;
      errors.push("Access Key is required.");
    }
    if (!formData.secretKey) {
      isValid = false;
      errors.push("Secret Key is required.");
    }
    if (!formData.email) {
      isValid = false;
      errors.push("Email is required.");
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      isValid = false;
      errors.push("Email is invalid.");
    }
    if (!formData.projectName) {
      isValid = false;
      errors.push("Project Name is required.");
    }
    // 추가적인 검증 로직을 여기에 추가할 수 있습니다.

    if (isValid) {
      alert('Form is valid!');
    } else {
      alert(`Form has errors:\n${errors.join('\n')}`);
    }
  };

  return <Button onClick={validate}>Validate</Button>;
};

export default ValidateButton;
