    const fetchProjects = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-project-name', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const projectName = response.data.project_name;
            setProjectName(projectName);
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    

    const fetchClusters = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-clusters', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            
            const clusterNames = response.data.items.map((item: any) => item.name);

            setClusterList(clusterNames); // 배열로 설정

        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

 

    const fetchInstanceLists = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-instance-groups', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const instanceSetNames = response.data.map((item: any) => item.name);
            setInstanceGroupList(instanceSetNames);
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const fetchInstanceEndpoints = async (selectedInstanceGroupName?: string) => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-instance-endpoints', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const instanceSet = response.data.find((item: any) => item.name === selectedInstanceGroupName);
              if (instanceSet) {
            setPrimaryEndpoint(instanceSet.primary_endpoint);
            setStandbyEndpoint(instanceSet.standby_endpoint);
              }
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const fetchKubeConfig = async (selectedClusterName?: string) => {
        setLoading(true);
        try {
            const response = await axios.post<KubeConfig>('http://61.109.237.248:8000/get-kubeconfig', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const { clusters } = response.data;
            const selectedCluster = clusters.find(cluster => cluster.name === selectedClusterName);
             if (selectedCluster) {
            setClusterName(selectedCluster.name);
            setApiEndpoint(selectedCluster.cluster.server);
            setAuthData(selectedCluster.cluster["certificate-authority-data"]);
             }
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const handleConsoleClick = (url: string) => {
        window.open(url, '_blank');
    };

    useEffect(() => {
        fetchClusters();
    }, []);

    const formData = {
        accessKey,
        secretKey,
        email,
        projectName,
        clusterName,
        apiEndpoint,
        authData,
        instanceList,
        primaryEndpoint,
        standbyEndpoint,
        dockerImageName,
        dockerJavaVersion,
    };

    
    return (
        <Container>
            <Header>kakaocloud 교육용</Header>
            <Title>Bastion VM 스크립트 생성</Title>
            <GroupContainer>
                <InputBox label="1. 사용자 액세스 키" placeholder="직접 입력" value={accessKey} onChange={(e) => setAccessKey(e.target.value)} />
                <InputBox label="2. 사용자 액세스 보안 키" placeholder="직접 입력" value={secretKey} onChange={(e) => setSecretKey(e.target.value)} />
                <InputBox label="3. 사용자 이메일" placeholder="직접 입력" value={email} onChange={(e) => setEmail(e.target.value)} />
            </GroupContainer>
            <GroupContainer>
                <InputBox
                    label="4. 프로젝트 이름"
                    placeholder="직접 입력"
                    value={projectName}
                    onChange={(e) => setProjectName(e.target.value)}
                    showApiButton
                    onApiClick={() => handleApiButtonClick('fetchProjects', handleFetchProjectsAndClusters)}
                    isLoading={loadingButton === 'fetchProjects'}
                    disableAll={!!loadingButton}
                />
            </GroupContainer>
            <GroupContainer>
                <SelectBox
                    label="5. 클러스터 리스트"
                    value={clusterName}
                    options={clusterList}
                    onChange={(e) => {
                        const selectedCluster = e.target.value;
                        setClusterName(selectedCluster);
                        handleApiButtonClick('fetchKubeConfig', fetchKubeConfig, selectedCluster);
                    }}
                    disabled={loadingButton === 'fetchClusters'}
                />
                <InputBox 
                    label="6. 클러스터 이름" 
                    placeholder="직접 입력" 
                    value={clusterName} 
                    onChange={(e) => setClusterName(e.target.value)} 
                    height="100px"
                    showApiButton
                    onApiClick={() => handleApiButtonClick('fetchClusterName', fetchKubeConfig, clusterName)}
                    isLoading={loadingButton === 'fetchClusterName'}
                    disableAll={!!loadingButton}
                />
                <InputBox 
                    label="7. 클러스터의 API 엔드포인트" 
                    placeholder="직접 입력" 
                    value={apiEndpoint} 
                    onChange={(e) => setApiEndpoint(e.target.value)} 
                    height="100px"
                    showApiButton
                    //onApiClick={() => handleApiButtonClick('fetchApiEndpoint', fetchKubeConfig, clusterName)}
                    //isLoading={loadingButton === 'fetchApiEndpoint'}
                    //disableAll={!!loadingButton}
                />
                <InputBox 
                    label="8. 클러스터의 certificate-authority-data" 
                    placeholder="직접 입력" 
                    value={authData} 
                    onChange={(e) => setAuthData(e.target.value)}
                    height="100px"
                    showApiButton
                    //onApiClick={() => handleApiButtonClick('fetchAuthData', fetchKubeConfig, clusterName)}
                    //isLoading={loadingButton === 'fetchAuthData'}
                    //disableAll={!!loadingButton}
                />

            </GroupContainer>
            <GroupContainer>
                 <SelectBox
                    label="9. 인스턴스 그룹 리스트"
                    value={instanceGroupName}
                    options={instanceGroupList}
                    onChange={handleInstanceGroupChange}
                    disabled={loadingButton === 'fetchInstanceLists'}
                />
                <InputBox 
                    label="10. Primary의 엔드포인트" 
                    placeholder="직접 입력" 
                    value={primaryEndpoint} 
                    onChange={(e) => setPrimaryEndpoint(e.target.value)}
                    height="100px"
                    showApiButton
                    onApiClick={() => handleApiButtonClick('fetchInstancePrimaryEndpoints', fetchInstanceEndpoints, instanceGroupName)}
                    isLoading={loadingButton === 'fetchInstancePrimaryEndpoints'}
                    disableAll={!!loadingButton}
                />
                <InputBox 
                    label="11. Standby의 엔드포인트" 
                    placeholder="직접 입력" 
                    value={standbyEndpoint} 
                    onChange={(e) => setStandbyEndpoint(e.target.value)} 
                    height="100px"
                    showApiButton
                    onApiClick={() => handleApiButtonClick('fetchInstanceStandbyEndpoints', fetchInstanceEndpoints, instanceGroupName)}
                    isLoading={loadingButton === 'fetchInstanceStandbyEndpoints'}
                    disableAll={!!loadingButton}
                />
            </GroupContainer>
            <GroupContainer>
                <InputBox label="12. Docker Image 이름" placeholder="직접 입력" value={dockerImageName} onChange={(e) => setDockerImageName(e.target.value)} />
                <InputBox label="13. Docker Image Base Java Version" placeholder="직접 입력" value={dockerJavaVersion} onChange={(e) => setDockerJavaVersion(e.target.value)} />
            </GroupContainer>
            <ButtonContainer>
                <StyledButton onClick={generateScript}>스크립트 생성 및 클립보드로 복사</StyledButton>
                <ValidateButton formData={formData} clusterList={clusterList}/>
            </ButtonContainer>
            <ScriptDisplay script={script} />
            <ButtonContainer>
            </ButtonContainer>
        </Container>
    );
};

export default MainPage;
