import React, { useState } from 'react';
import InputBox from '../components/InputBox';
import ScriptDisplay from '../components/ScriptDisplay';
import CopyButton from '../components/CopyButton';
import styled from 'styled-components';
import axios from 'axios';

const Container = styled.div`
    max-width: 800px;
    margin: 2em auto;
    padding: 2em;
    background-color: rgba(0, 0, 0, 0.5);
    border-radius: 8px;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.1);
    z-index: 10;
    min-height: 100vh;
`;

const Title = styled.h1`
    text-align: center;
    margin-bottom: 1.5em;
    color: #fff;
`;

const GroupContainer = styled.div`
    margin-bottom: 1.5em;
    padding: 1em;
    padding-top: 2em;
    padding-bottom: 0.01em;
    background-color: rgba(255, 255, 255, 0.1);
    border-radius: 8px;
`;

const ButtonContainer = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin-top: 2em;
`;

const StyledButton = styled.button`
    background-color: #28a745;
    color: white;
    border: none;
    padding: 0.75em 1.5em;
    border-radius: 4px;
    cursor: pointer;
    font-size: 1em;
    transition: background-color 0.1s ease-in;
    margin: 0 1em;

    &:hover {
        background-color: #218838;
    }

    &:focus {
        outline: none;
        box-shadow: 0 0 8px rgba(33, 136, 56, 0.6);
    }
`;

interface KubeConfig {
    clusters: Array<{
        cluster: {
            "certificate-authority-data": string;
            server: string;
        };
        name: string;
    }>;
}

const MainPage: React.FC = () => {
    const [accessKey, setAccessKey] = useState('');
    const [secretKey, setSecretKey] = useState('');
    const [email, setEmail] = useState('');
    const [projectName, setProjectName] = useState('');
    const [clusterList, setClusterList] = useState('');
    const [clusterName, setClusterName] = useState('');
    const [apiEndpoint, setApiEndpoint] = useState('');
    const [authData, setAuthData] = useState('');
    const [instanceList, setInstanceList] = useState('');
    const [primaryEndpoint, setPrimaryEndpoint] = useState('');
    const [standbyEndpoint, setStandbyEndpoint] = useState('');
    const [dockerImageName, setDockerImageName] = useState('demo-spring-boot');
    const [dockerJavaVersion, setDockerJavaVersion] = useState('17-jdk-slim');
    const [script, setScript] = useState('');
    const [loading, setLoading] = useState(false);

    const generateScript = () => {
        const newScript = `#!/bin/bash
echo "kakaocloud: 1.Starting environment variable setup"
# 환경 변수 설정: 사용자는 이 부분에 자신의 환경에 맞는 값을 입력해야 합니다.
command=$(cat <<EOF
export ACC_KEY='${accessKey}'
export SEC_KEY='${secretKey}'
export EMAIL_ADDRESS='${email}'
export CLUSTER_NAME='${clusterName}'
export API_SERVER='${apiEndpoint}'
export AUTH_DATA='${authData}'
export PROJECT_NAME='${projectName}'
export INPUT_DB_EP1='${primaryEndpoint}'
export INPUT_DB_EP2='${standbyEndpoint}'
export DOCKER_IMAGE_NAME='${dockerImageName}'
export DOCKER_JAVA_VERSION='${dockerJavaVersion}'
export JAVA_VERSION='17'
export SPRING_BOOT_VERSION='3.1.0'
export DB_EP1=\$(echo -n "\$INPUT_DB_EP1" | base64 -w 0)
export DB_EP2=\$(echo -n "\$INPUT_DB_EP2" | base64 -w 0)
EOF
)
eval "$command"
echo "$command" >> /home/ubuntu/.bashrc
echo "kakaocloud: Environment variable setup completed"
echo "kakaocloud: 2.Checking the validity of the script download site"
curl --output /dev/null --silent --head --fail "https://github.com/kakaocloud-edu/tutorial/raw/main/AdvancedCourse/src/script/script.sh" || { echo "kakaocloud: Script download site is not valid"; exit 1; }
echo "kakaocloud: Script download site is valid"
wget https://github.com/kakaocloud-edu/tutorial/raw/main/AdvancedCourse/src/script/script.sh
chmod +x script.sh
sudo -E ./script.sh`;
        setScript(newScript);
    };

    const fetchProjects = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-projects', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const projectNames = response.data.projects.map((project: any) => project.name);
            setProjectName(projectNames.join(', '));
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const fetchClusters = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-clusters', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const clusterNames = response.data.items.map((item: any) => item.name);
            setClusterList(clusterNames.join(', '));
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const fetchInstanceLists = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-instance-groups', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const instanceSetNames = response.data.join(', ');
            setInstanceList(instanceSetNames);
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };


    const fetchInstanceEndpoints = async () => {
        setLoading(true);
        try {
            const response = await axios.post('http://61.109.237.248:8000/get-instance-endpoints', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            
            setPrimaryEndpoint(response.data.primary_endpoint);
            setStandbyEndpoint(response.data.standby_endpoint);
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const fetchKubeConfig = async () => {
        setLoading(true);
        try {
            const response = await axios.post<KubeConfig>('http://61.109.237.248:8000/get-kubeconfig', {
                access_key_id: accessKey,
                access_key_secret: secretKey,
            });
            const { clusters } = response.data;
            if (clusters.length > 0) {
                setClusterName(clusters[0].name);
                setApiEndpoint(clusters[0].cluster.server);
                setAuthData(clusters[0].cluster["certificate-authority-data"]);
            }
        } catch (error) {
            console.error('API 호출 오류:', error);
        }
        setLoading(false);
    };

    const handleConsoleClick = (url: string) => {
        window.open(url, '_blank');
    };

    return (
        <Container>
            <Title>Bastion VM 스크립트 생성</Title>
            <GroupContainer>
                <InputBox label="사용자 액세스 키" placeholder="직접 입력" value={accessKey} onChange={(e) => setAccessKey(e.target.value)} />
                <InputBox label="사용자 액세스 보안 키" placeholder="직접 입력" value={secretKey} onChange={(e) => setSecretKey(e.target.value)} />
                <InputBox label="사용자 이메일" placeholder="직접 입력" value={email} onChange={(e) => setEmail(e.target.value)} />
            </GroupContainer>
            <GroupContainer>
                <InputBox
                    label="프로젝트 이름"
                    placeholder="직접 입력"
                    value={projectName}
                    onChange={(e) => setProjectName(e.target.value)}
                    showApiButton
                    onApiClick={fetchProjects}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading}
                />
            </GroupContainer>
            <GroupContainer>
                <InputBox
                    label="클러스터 리스트"
                    placeholder="직접 입력"
                    value={clusterList}
                    onChange={(e) => setClusterList(e.target.value)}
                    height="100px"
                    showApiButton
                    onApiClick={fetchClusters}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading}
                />
                <InputBox 
                    label="클러스터 이름" 
                    placeholder="직접 입력" 
                    value={clusterName} 
                    onChange={(e) => setClusterName(e.target.value)} 
                    height="100px"
                    showApiButton
                    onApiClick={fetchKubeConfig}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading}/>
                <InputBox 
                    label="클러스터의 API 엔드포인트" 
                    placeholder="직접 입력" 
                    value={apiEndpoint} 
                    onChange={(e) => setApiEndpoint(e.target.value)} 
                    height="100px"
                    showApiButton
                    onApiClick={fetchKubeConfig}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading}
                    />
                <InputBox 
                    label="클러스터의 certificate-authority-data" 
                    placeholder="직접 입력" 
                    value={authData} 
                    onChange={(e) => setAuthData(e.target.value)}
                    height="100px"
                    showApiButton
                    onApiClick={fetchKubeConfig}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading} 
                    />
            </GroupContainer>
            <GroupContainer>
                <InputBox
                    label="인스턴스 그룹 리스트"
                    placeholder="직접 입력"
                    value={instanceList}
                    onChange={(e) => setInstanceList(e.target.value)}
                    height="100px"
                    showApiButton
                    onApiClick={fetchInstanceLists}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading}
                />
                <InputBox 
                    label="Primary의 엔드포인트" 
                    placeholder="직접 입력" value={primaryEndpoint} 
                    onChange={(e) => setPrimaryEndpoint(e.target.value)}
                    height="100px"
                    showApiButton
                    onApiClick={fetchInstanceEndpoints}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading} />
                <InputBox 
                    label="Standby의 엔드포인트" 
                    placeholder="직접 입력" 
                    value={standbyEndpoint} 
                    onChange={(e) => setStandbyEndpoint(e.target.value)} 
                    height="100px"
                    showApiButton
                    onApiClick={fetchInstanceEndpoints}
                    onConsoleClick={() => handleConsoleClick('https://github.com/kakaocloud-edu/tutorial/blob/main/AdvancedCourse/PracticalTextbook/Lab03.md#1-bastion-vm-%EC%9D%B8%EC%8A%A4%ED%84%B4%EC%8A%A4-%EC%83%9D%EC%84%B1')}
                    isLoading={loading}/>
            </GroupContainer>
            <GroupContainer>
                <InputBox label="Docker Image 이름" placeholder="직접 입력" value={dockerImageName} onChange={(e) => setDockerImageName(e.target.value)} />
                <InputBox label="Docker Image Base Java Version" placeholder="직접 입력" value={dockerJavaVersion} onChange={(e) => setDockerJavaVersion(e.target.value)} />
            </GroupContainer>
            <ButtonContainer>
                <StyledButton onClick={generateScript}>스크립트 생성</StyledButton>
                <CopyButton script={script} />
            </ButtonContainer>
            <ScriptDisplay script={script} />
        </Container>
    );
};

export default MainPage;
